var log4js = require('log4js');

/**
 * 调试http请求的数据包，通常配置在config.json中
 * "logger": { //关闭默认的logger
 *     "enabled": false
 * },
 *
 * "log4js": { //使用log4js的配置
 *     "enabled": true,
 *     "priority": 51,
 *     "module": {
 *         "name": "kraken-middleware/lib/log-mid",
 *         "arguments": [{
 *             "category": "http",
 *             "level": "auto"
 *         }]
 *     }
 * },
 */
module.exports = function(options) {
	return log4js.connectLogger(log4js.getLogger(options.category), {
		level: options.level
	});
};