/**
 * 通过该中间件控制是否开启某个功能或者某个页面的展示，配置在config.json中
 * "switchOption": {
 *     "enabled": true,
 *     "priority": 119,
 *     "module": {
 *         "name": "kraken-middleware/lib/switch",
 *         "arguments": [{}]
 *     }
 * },
 */
module.exports = function(options) {
	return function switchOption(req, res, next) {
		res.locals.__switch__ = options;
		next();
	};
};