'use strict';

var cipher = require('../lib/index.js');

/*
  ======== A Handy Little Nodeunit Reference ========
  https://github.com/caolan/nodeunit

  Test methods:
    test.expect(numAssertions)
    test.done()
  Test assertions:
    test.ok(value, [message])
    test.equal(actual, expected, [message])
    test.notEqual(actual, expected, [message])
    test.deepEqual(actual, expected, [message])
    test.notDeepEqual(actual, expected, [message])
    test.strictEqual(actual, expected, [message])
    test.notStrictEqual(actual, expected, [message])
    test.throws(block, [error], [message])
    test.doesNotThrow(block, [error], [message])
    test.ifError(value)
*/

exports['cipher'] = {
  setUp: function(done) {
    // setup here
    done();
  },
  'md5': function(test) {
    var mw = cipher.md5['sign']('test', 'a');
    test.equal(cipher.md5['verify']('test', 'a', mw), true, 'should be true.');
    test.done();
  },
  'base64': function(test) {
    var mw = cipher.base64['+']('testabc');
    test.equal(cipher.base64['-'](mw), 'testabc', 'should be testabc.');
    test.done();
  },
};