// var mongoose = require('mongoose');

module.exports = function(mongoose){
	var RBAC_RoleSchema = mongoose.Schema({
		name: {
			type: String,
			unique: true
		},
		desc: String, //角色描述
		auth: { //该角色有的权限
			menu: [], //菜单数组，里面是菜单编号 如[1001,1002]
			operate: [], //操作数组，里面是操作名称,如[1001_sh,1002_hd],名称对应系统定义的操作名
			uri: [] //资源数组，里面是资源名称，如['GET/account/login/:name','POST/account/login/:name']
		}
	});

	return mongoose.model('RBAC_role', RBAC_RoleSchema, 'rbac_role');
};
